package main

import (
	"context"
	"fmt"
	"time"

	"k8s.io/apimachinery/pkg/api/errors"
	metav1 "k8s.io/apimachinery/pkg/apis/meta/v1"
	"k8s.io/client-go/kubernetes"
	"k8s.io/client-go/rest"
)

func main() {
	// Create a new client for interacting with the Kubernetes API.
	config, err := rest.InClusterConfig()
	if err != nil {
		panic(err)
	}
	client, err := kubernetes.NewForConfig(config)
	if err != nil {
		panic(err)
	}

	// Set up a watcher to watch for StatefulSets that are stuck starting.
	watcher, err := client.AppsV1().StatefulSets("default").Watch(context.TODO(), metav1.ListOptions{})
	if err != nil {
		panic(err)
	}

	// Loop indefinitely, waiting for events from the watcher.
	for event := range watcher.ResultChan() {
		// If the event is an update to a StatefulSet's status, check if it's stuck.
		if event.Type == "MODIFIED" {
			statefulSet, ok := event.Object.(*appsv1.StatefulSet)
			if !ok {
				continue
			}
			if isStuckStarting(statefulSet) {
				fmt.Printf("Deleting stuck StatefulSet %s\n", statefulSet.Name)
				err := client.AppsV1().StatefulSets("default").Delete(context.TODO(), statefulSet.Name, metav1.DeleteOptions{})
				if err != nil {
					fmt.Printf("Error deleting stuck StatefulSet: %s\n", err)
				}
			}
		}
	}
}

func isStuckStarting(statefulSet *appsv1.StatefulSet) bool {
	// Check if the StatefulSet is stuck by comparing the current number of replicas to the desired number.
	if statefulSet.Status.ReadyReplicas < statefulSet.Spec.Replicas {
		// Check if the StatefulSet has been stuck for more than 5 minutes.
		if time.Since(statefulSet.CreationTimestamp.Time) > time.Minute*5 {
			return true
		}
	}
	return false
}
```
This operator watches for updates to StatefulSets in the "default" namespace, and if it sees an update to a StatefulSet's status, it checks if the StatefulSet is stuck starting by comparing the current number of replicas to the desired number. If the StatefulSet is stuck and has been stuck for more than 5 minutes, the operator deletes it.

